                var webView = new WKWebView(rect, new WKWebViewConfiguration())
                {
                    CustomUserAgent = "Mozilla/5.0 " +
                    "(Macintosh; Intel Mac OS X 10_13_6) AppleWebKit/605.1.15 " +
